mapper.select:
SELECT hc.my_habit_id, hc.habit_id, hc.user_id, hc.check_date, h.habit_title, h.category_title FROM `HabitCheck` hc JOIN `Habit` h ON hc.habit_id = h.habit_id WHERE hc.user_id = #{userId}
AND DATE(hc.check_date) = DATE(#{checkDate})
AND DATE(hc.check_date) = CURDATE()

SELECT grp AS timeDiff, COUNT(*) AS continuousDateCount
FROM (
         SELECT
             check_date,
             DATEDIFF(CURDATE(), check_date) -
             (ROW_NUMBER() OVER (ORDER BY check_date DESC) - 1) AS grp
         FROM (
                  SELECT DISTINCT DATE(check_date) AS check_date
             FROM HabitCheck
         WHERE user_id = #{userId}
           AND habit_id = #{habitId}
           AND DATE(check_date) BETWEEN DATE_SUB(CURDATE(), INTERVAL 32 DAY) AND CURDATE()
     ) sub
    ) t
GROUP BY grp
ORDER BY grp;

SELECT COUNT(*)
FROM HabitCheck
WHERE user_id = #{userId}

SELECT IFNULL(SUM(h.save_amount), 0) AS total_save_amount FROM HabitCheck hc JOIN Habit h ON hc.habit_id = h.habit_id WHERE hc.user_id = #{userId}
AND DATE(hc.check_date) = DATE(#{checkDate})
AND DATE(hc.check_date) = CURDATE()

SELECT COUNT(*)
FROM HabitCheck
WHERE user_id = #{userId} AND habit_id = #{habitId}
