SELECT h.habit_title, h.category_title
FROM `HabitCheck` hc JOIN `MyHabit` mh ON hc.my_habit_id = mh.my_habit_id JOIN `Habit` h ON mh.habit_id = h.habit_id
WHERE DATE(hc.check_date) = CURDATE() AND user_id = #{userId};

