mapper.insert:
INSERT INTO Habit (user_id, habit_title, category_title)
VALUES (#{userId}, #{habitTitle}, #{categoryTitle});

INSERT INTO MyHabit (user_id, habit_id, state)
VALUES (#{userId}, LAST_INSERT_ID(), '대기');

mapper.update:
UPDATE MyHabit
SET state = "진행"
WHERE my_habit_id = #{myHabitId};

UPDATE MyHabit
SET state = "대기"
WHERE my_habit_id = #{myHabitId};
