mapper.update:
UPDATE MyHabit
SET state = #{state}
WHERE my_habit_id = #{myHabitId}

UPDATE MyHabit
SET state = 'ON'
WHERE my_habit_id = #{myHabitId};

UPDATE MyHabit
SET state = 'OFF'
WHERE my_habit_id = #{myHabitId};

mapper.select:
SELECT h.habit_title FROM MyHabit mh JOIN Habit h ON mh.habit_id = h.habit_id WHERE mh.state = 'on'
SELECT * FROM MyHabit WHERE my_habit_id = #{myHabitId}
SELECT * FROM MyHabit
