mapper.select:
SELECT pc.* FROM PostCommunity pc JOIN Habit h ON pc.habit_id = h.habit_id WHERE 1=1
AND pc.user_id = #{userId}
AND h.category_title = #{categoryTitle}
ORDER BY pc.created_at DESC

SELECT COUNT(*)
FROM PostCommunity
WHERE user_id = #{userId}

mapper.update:
UPDATE PostCommunity
SET user_id = #{userId}, habit_id = #{habitId}, post_likes = #{postLikes},
    image_url = #{imageURL}, content = #{content}, hashtag = #{hashtag}, created_at = #{createdAt}
WHERE post_id = #{postId}

mapper.select:
SELECT pc.image_url, pc.created_at
FROM postcommunity pc
         JOIN myhabit mh ON pc.habit_id = mh.habit_id
WHERE mh.user_id = #{userId}
    AND MONTH(pc.created_at) = #{month}
  AND YEAR(pc.created_at) = #{year}
  AND mh.habit_id = #{habitId}

SELECT p.post_id, p.created_at, p.image_url, p.post_likes, p.content
FROM postcommunity p
         JOIN (
    SELECT DATE(created_at) AS created_date, MAX(post_likes) AS max_likes
    FROM postcommunity
WHERE user_id = #{userId}
  AND MONTH(created_at) = #{month}
  AND YEAR(created_at) = #{year}
GROUP BY DATE(created_at)
    ) max_likes_per_day
ON DATE(p.created_at) = max_likes_per_day.created_date
    AND p.post_likes = max_likes_per_day.max_likes

SELECT p.post_id, p.created_at, p.image_url, p.post_likes, p.content
FROM postcommunity p
         JOIN (
    SELECT DATE(created_at) AS created_date, MAX(post_likes) AS max_likes
    FROM postcommunity
WHERE user_id = #{userId}
  AND YEAR(created_at) = #{year}
  AND MONTH(created_at) = #{month}
  AND DAY(created_at) = #{dt}
GROUP BY DATE(created_at)
    ) max_likes_per_day
ON DATE(p.created_at) = max_likes_per_day.created_date
    AND p.post_likes = max_likes_per_day.max_likes
